injectjs.script:
var settings = null;
var eventInterval = null;

function event_main(settings) {
    if ($('.ticketCard button[type="button"]').length) {
        webdriver_click(settings, '.ticketCard button[type="button"]');
        clearInterval(eventInterval);
    }
}

function event_loop() {
    chrome.storage.local.get('status', function(items) {
        if (items.status && items.status == 'ON') {
            event_main(settings);
        } else {
            //console.log('ddddext status is not OFF');
        }
    });
}

var settings;
chrome.storage.local.get('settings', function(items) {
    if (items.settings) {
        settings = items.settings;
        if (settings) {
            eventInterval = setInterval(() => {
                event_loop();
            }, 100);
        }
    }
});

// 排除關鍵字
// "Restricted View" 視線不完整的區域，如果是在「第一波」的搶票，建議加入排除的清單之中。
let exclude_keyword_array = ["輪椅", "身障", "障礙", "Wheelchair", "燈柱遮蔽", "視線不完整"];
// 票價關鍵字
let area_keyword_array = [];
// 允許不連續座位, false = 不打勾 = 要求連續； true = 會打勾 = 允許不連續座位。
let disable_adjacent_seat = false;
// 排序方式
// 可使用的選項: "from top to bottom" / "from bottom to top" / "center" / "random"
let order_mode = "random";
// 張數
let ticket_number = 2;
// =========== 以下為程式碼 ===========
var settings = null;
var performanceInterval = null;

function cityline_clean_exclude(exclude_keyword_array) {
    for (let i = 0; i < exclude_keyword_array.length; i++) {
        const current_keyword = exclude_keyword_array[i].toLowerCase().replace(/,/g, "");
        $("div.price > div.form-check").each(function() {
            let html_text = $(this).text().toLowerCase().toLowerCase().replace(/,/g, "");
            let keyword_control_symbol = false;
            // AND logic
            if (current_keyword.indexOf(" ") > 0) {
                keyword_control_symbol = true;
                const keyword_array = current_keyword.split(" ");
                let contain = true;
                for (var j = 0; j < keyword_array.length; j++) {
                    if (keyword_array[j] == "") continue;
                    if (html_text.indexOf(keyword_array[j]) == -1) {
                        contain = false;
                        break;
                    }
                }
                if (contain) {
                    $(this).remove();
                }
            }
            // OR logic
            if (current_keyword.indexOf("|") > 0) {
                keyword_control_symbol = true;
                const regex = new RegExp(current_keyword);
                if (regex.test(html_text)) {
                    $(this).remove();
                }
            }
            // normal keyword
            if (!keyword_control_symbol) {
                if (html_text.indexOf(current_keyword) > -1) {
                    $(this).remove();
                }
            }
        });
    }
}

function cityline_area_keyword(area_keyword_array) {
    //console.log(area_keyword_array);
    let target_area = null;
    let matched_block = [];
    let query_string = "div.price > div.form-check";
    if (area_keyword_array.length) {
        for (let i = 0; i < area_keyword_array.length; i++) {
            const current_keyword = area_keyword_array[i].toLowerCase().replace(/,/g, "");
            $(query_string).each(function() {
                let html_text = $(this).text().toLowerCase().replace(/,/g, "");
                let html_string = $(this).html().toLowerCase().replace(/,/g, "");
                let is_soldout = false;
                //console.log("html_text:"+html_text);
                if (html_text.indexOf('售罄') > -1) {
                    is_soldout = true;
                }
                if (html_string.indexOf('>sold out<') > -1) {
                    is_soldout = true;
                }
                if (!is_soldout) {
                    let keyword_control_symbol = false;
                    // AND logic
                    if (current_keyword.indexOf(" ") > 0) {
                        keyword_control_symbol = true;
                        const keyword_array = current_keyword.split(" ");
                        let contain = true;
                        for (var j = 0; j < keyword_array.length; j++) {
                            if (keyword_array[j] == "") continue;
                            if (html_text.indexOf(keyword_array[j]) == -1) {
                                contain = false;
                                break;
                            }
                        }
                        if (contain) {
                            matched_block.push($(this));
                        }
                    }
                    // OR logic
                    if (current_keyword.indexOf("|") > 0) {
                        keyword_control_symbol = true;
                        const regex = new RegExp(current_keyword);
                        if (regex.test(html_text)) {
                            matched_block.push($(this));
                        }
                    }
                    // normal keyword
                    if (!keyword_control_symbol) {
                        if (html_text.indexOf(current_keyword) > -1) {
                            matched_block.push($(this));
                        }
                    }
                }
                target_area = get_target_item_with_order(order_mode, matched_block);
            });

            if (matched_block.length) {
                //console.log("match keyword:" + area_keyword_array[i]);
                break;
            }
        }
    } else {
        $(query_string).each(function() {
            let html_text = $(this).text().toLowerCase().replace(/,/g, "");
            let html_string = $(this).html().toLowerCase().replace(/,/g, "");
            let is_soldout = false;
            //console.log("html_text:"+html_text);
            if (html_text.indexOf('售罄') > -1) {
                is_soldout = true;
            }
            if (html_string.indexOf('>sold out<') > -1) {
                is_soldout = true;
            }
            if (!is_soldout) {
                matched_block.push($(this));
            }
        });
        target_area = get_target_item_with_order(order_mode, matched_block);
    }

    if (target_area) {
        target_area.find("input").click();
    } else {
        //console.log("not target_area found.")
    }
}

function get_performance_status() {
    const loadingoverlay = $(".loadingoverlay");
    const loading_display = loadingoverlay.css("display");
    let able_to_work = true;
    if (loading_display && loading_display == "flex") {
        able_to_work = false;
    }
    return able_to_work;
}

function find_performance_target() {
    cityline_area_keyword(area_keyword_array);

    let target_row = $("#ticketType0");
    let ticket_options = target_row.find("option");
    if (ticket_options.length) {
        let is_ticket_number_assign = false;
        if (ticket_number > 0) {
            ticket_options.each(function() {
                if ($(this).val() == ticket_number) {
                    $(this).prop('selected', true);
                    $(this).click();
                    target_row.trigger("change");
                    is_ticket_number_assign = true;
                    return false;
                }
            });
        }
        if (!is_ticket_number_assign) {
            ticket_options.last().prop('selected', true);
            target_row.trigger("change");
        }
    }

    if (disable_adjacent_seat) {
        $('input[type=checkbox]:checked').each(function() {
            $(this).click();
        });
    }

    //console.log("selected ticket number:" + $("#ticketType0").val());
    const selected_ticket_count = $("#ticketType0").val();
    if (selected_ticket_count && parseInt(selected_ticket_count) > 0) {
        const selector = "#expressPurchaseBtn:not(.d-none)";
        console.log("press expressPurchaseBtn:" + selected_ticket_count);
        webdriver_click(settings, selector);
        //clearInterval(performanceInterval);
    }
}

function performance_main(settings) {
    console.log("performance_main");
    cityline_clean_exclude(exclude_keyword_array);

    if ($('#expressPurchaseBtn:not(.d-none)').length) {
        if (get_performance_status()) {
            find_performance_target();
        }
    }
}

function performance_loop() {
    chrome.storage.local.get('status', function(items) {
        if (items.status && items.status == 'ON') {
            performance_main(settings);
        } else {
            console.log('ddddext status is not OFF');
        }
    });
}

var settings;
chrome.storage.local.get('settings', function(items) {
    if (items.settings) {
        settings = items.settings;
        if (settings) {
            performanceInterval = setInterval(() => {
                performance_loop();
            }, 100);
        }
    }
});
