var tzuchi_ocr_config = {
    captcha_length: 5,
    captcha_selector: "#imgVI",
    captcha_renew_selector: "#Button4",
    input_selector: "#txtVCode",
    submit_selector: "#btnRegNo"
};
var tzuchi_ocr_config_org = {
    captcha_length: 5,
    captcha_selector: "#MainContent_imgVI",
    captcha_renew_selector: "#MainContent_btnReflash",
    input_selector: "#MainContent_tbxVCode",
    submit_selector: "#MainContent_btnRegNo"
};

function isNumeric(value) {
    return /^-?\d+$/.test(value);
}
var tzuchi_ocr_interval = null;

function get_tzuchi_ocr_image() {
    // due to multi format
    const currentUrl = window.location.href;
    const domain = currentUrl.split('/')[2];
    let captcha_selector = tzuchi_ocr_config.captcha_selector;
    if (domain.indexOf(".org.tw") > -1) {
        captcha_selector = tzuchi_ocr_config_org.captcha_selector;
    }

    let image_data = "";
    //console.log(captcha_selector);
    let img = document.querySelector(captcha_selector);
    if (img != null) {
        let canvas = document.createElement('canvas');
        let context = canvas.getContext('2d');
        canvas.height = img.naturalHeight;
        canvas.width = img.naturalWidth;
        context.drawImage(img, 0, 0);
        let img_data = canvas.toDataURL();
        if (img_data) {
            image_data = img_data.split(",")[1];
            //console.log(image_data);
        }
    } else {
        // console.log("img not found:" + tzuchi_ocr_config.captcha_selector);
    }
    return image_data;
}

var last_tzuchi_captcha_answer = "";
chrome.runtime.onMessage.addListener((message) => {
    // due to multi format
    const currentUrl = window.location.href;
    const domain = currentUrl.split('/')[2];
    let captcha_renew_selector = tzuchi_ocr_config.captcha_renew_selector;
    if (domain.indexOf(".org.tw") > -1) {
        captcha_renew_selector = tzuchi_ocr_config_org.captcha_renew_selector;
    }

    //console.log('sent from background', message);
    if (message && message.hasOwnProperty("answer")) {
        let is_valid_anwser = false;
        if (message.answer.length == tzuchi_ocr_config.captcha_length) {
            if (isNumeric(message.answer)) {
                is_valid_anwser = true;
            }
        }
        //console.log(is_valid_anwser);
        if (is_valid_anwser) {
            tzuchi_set_ocr_answer(message.answer);
            last_tzuchi_captcha_answer = message.answer;
        } else {
            // renew captcha.
            if (last_tzuchi_captcha_answer != message.answer) {
                last_tzuchi_captcha_answer = message.answer;
                console.log("renew captcha: " + captcha_renew_selector);
                if ($(captcha_renew_selector).length) {
                    $(captcha_renew_selector).click();
                }
                tzuchi_area_main();
            }
        }
    }
});

function tzuchi_set_ocr_answer(answer) {
    // due to multi format
    const currentUrl = window.location.href;
    const domain = currentUrl.split('/')[2];
    let input_selector = tzuchi_ocr_config.input_selector;
    let submit_selector = tzuchi_ocr_config.submit_selector;
    if (domain.indexOf(".org.tw") > -1) {
        input_selector = tzuchi_ocr_config_org.input_selector;
        submit_selector = tzuchi_ocr_config_org.submit_selector;
    }

    //console.log("answer:"+answer);
    if (answer.length > 0) {
        let sendkey_by_webdriver = false;
        if (settings) {
            if (settings.hasOwnProperty("token")) {
                sendkey_by_webdriver = true;
            }
        }
        //console.log("sendkey_by_webdriver:"+sendkey_by_webdriver);
        if (!sendkey_by_webdriver) {
            // solution #1: javascript.
            $(input_selector).val(answer);
            $(submit_selector).click();
        } else {
            // solution #2: click webdriver.
            //webdriver_location_sendkey(settings, input_selector, answer, document.location.href);
            //webdriver_location_click(settings, submit_selector, document.location.href);
            $(input_selector).val(answer);
            $(submit_selector).click();
        }
    }
}

async function tzuchi_get_ocr_answer(api_url, image_data) {
    let bundle = {
        action: 'ocr',
        data: {
            'url': api_url + 'ocr',
            'image_data': image_data,
        }
    };
    const return_answer = await chrome.runtime.sendMessage(bundle);
}

function tzuchi_orc_image_ready(api_url) {
    let ret = false;
    let image_data = get_tzuchi_ocr_image();
    if (image_data.length > 0) {
        ret = true;
        if (tzuchi_ocr_interval) clearInterval(tzuchi_ocr_interval);
        tzuchi_get_ocr_answer(api_url, image_data);
    }
    //console.log("orc_image_ready:"+ret);
    return ret;
}

function tzuchi_area_main() {
    let remote_url_string = get_remote_url(settings);
    tzuchi_ocr_interval = setInterval(() => {
        tzuchi_orc_image_ready(remote_url_string);
    }, 100);
}

storage.get('settings', function(items) {
    if (items.settings) {
        settings = items.settings;
        tzuchi_area_main();
    } else {
        console.log('no settings found');
    }
});

